import {React,useState} from 'react';
import Axios from 'axios'



const cors = require("cors");
const express = require("express");
const mysql = require("mysql");
require("dotenv").config();
const path = require("path");;

const app = express();
app.use(cors());
app.use(express.json());

//Listen on port 3000 
const listener = app.listen(process.env.PORT || 3000, () => {
  console.log("App is listening on port " + listener.address().port);
    });

function SignUp() {
  const [usernameReg, setUsernameReg ] = useState('');
  const [passwordReg, setPasswordReg ] = useState('');
  const [firstnameReg, setFirstNameReg ] = useState('');
  const [lastnameReg, setLastNameReg ] = useState('');
  const [emailReg, setEmailReg ] = useState('');
  const [mailingaddressReg, setMailingAddressReg ] = useState('');
  const [cityReg, setcityReg] = useState('');
  const [countyReg, setcountyReg] = useState('');
  const [zipReg, setzipReg] = useState('');
  const [stateReg, setstateReg] = useState('');



  const membersignup = () => { Axios.post("http://localhost:3000/members", {
    username: usernameReg,
    password: passwordReg,
    mbrfirstname: firstnameReg, 
    mbrlastname: lastnameReg, 
    email: emailReg, 
    address: mailingaddressReg,
    city: cityReg,
    county: countyReg,
    zip: zipReg,
    state: stateReg, 

    }).then((response) => {
      console.log(response);
    });
};
    return(
      <div className="container border" style={{marginTop:"50px",width:"80%", background:"#6D7A69"}}>
            <center><h1> Create a Teeny Tiny Explorers Account</h1> </center>

            <form className="row" style={{margin: "25px 85px 75px 100px"}}>
                  <label>Username</label>
                  <input type="text" name="username" placeholder="Username" className="form-control" onChange={(e) => {
                    setFirstNameReg(e.target.value);
                  }}
                  />

                  <label>First Name</label>
                  <input type="text" name="mbrfirstname"  className="form-control" onChange={(e) => {
                    setFirstNameReg(e.target.value);
                  }}
                  />

                  <label>Last Name</label>
                  <input type="lastname" name="mbrlastname" placeholder="Last Name" className="form-control" onChange={(e) => {
                    setLastNameReg(e.target.value);
                    }}
                     />

                  <label>Email</label>
                  <input type="email" name="email" placeholder="Enter email" className="form-control" onChange={(e) => {
                    setEmailReg(e.target.value);
                    }} 
                    />

                  <label>Password</label>
                  <input type="text" name="password" placeholder="Password" className="form-control" onChange={(e) => {
                    setPasswordReg(e.target.value);
                    }}
                    />


                  <label>Mailing Address</label>
                  <input type="text" name="address" placeholder="Mailing Address" className="form-control" onChange={(e) => {
                    setMailingAddressReg(e.target.value);
                  }}
                  />

                  <label>City</label>
                  <input type="text" name="city" className="form-control"onChange={(e) => {
                    setcityReg(e.target.value);
                  }}/>

                  <label>County</label>
                  <input type="text" name="county" className="form-control" onChange={(e) => {
                    setcountyReg(e.target.value);
                  }}/>

                  <label>State</label>
                  <select type="select" name="state" className="form-control" defaultValue="Choose..." onChange={(e) => {
                    setstateReg(e.target.value);
                  }}>
                    <option>Choose...</option>
                    <option>AL</option>
                    <option>AK</option>
                    <option>AZ</option>
                    <option>AR</option>
                    <option>CA</option>
                    <option>CO</option>
                    <option>CT</option>
                    <option>FL</option>
                    <option>GA</option>
                    <option>HI</option>
                    <option>ID</option>
                    <option>IL</option>
                    <option>IN</option>
                    <option>IA</option>
                    <option>KS</option>
                    <option>KY</option>
                    <option>LA</option>
                    <option>ME</option>
                    <option>MD</option>
                    <option>MA</option>
                    <option>MI</option>
                    <option>MN</option>
                    <option>MS</option>
                    <option>MO</option>
                    <option>MT</option>
                    <option>NE</option>
                    <option>NV</option>
                    <option>NH</option>
                    <option>NJ</option>
                    <option>NM</option>
                    <option>NY</option>
                    <option>NC</option>
                    <option>ND</option>
                    <option>OH</option>
                    <option>OK</option>
                    <option>OR</option>
                    <option>PA</option>
                    <option>RI</option>
                    <option>SC</option>
                    <option>SD</option>
                    <option>TN</option>
                    <option>TX</option>
                    <option>UT</option>
                    <option>VT</option>
                    <option>VA</option>
                    <option>WA</option>
                    <option>WV</option>
                    <option>WI</option>
                    <option>WY</option>
                  </select>

                  <label>Zip</label>
                  <input className="form-control" name="zip" type="text" onChange={(e) => {
                    setzipReg(e.target.value);
                  }} />

            <center>

              <input type="submit"  onClick= "membersignup" value="Submit" className="btn btn-primary" style={{marginTop: "30px",background:"white", color:"black"}} />


              <input type="submit" value="Submit" className="btn btn-primary" style={{marginTop: "30px",background:"white", color:"black"}} />

            </center>

          </form>
    </div>
  );
};
export default SignUp;
